import React, { useState, useEffect } from 'react';
import { Row, Col, Card, Typography, Image } from 'antd';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import productService from '../services/product.service';

const { Title } = Typography;

const ShelfManagement = () => {
  const [shelves, setShelves] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const fetchedProducts = await productService.getAll();
      const cleanJsonString = fetchedProducts.replace(/^<!--\s*|\s*-->$/g, '');
      const data = JSON.parse(cleanJsonString);
      
      // Create 6 shelves with 10 unique products each
      const newShelves = Array(6).fill().map((_, index) => ({
        id: `shelf-${index + 1}`,
        name: `Shelf ${index + 1}`,
        products: generateProducts(data, index)
      }));

      setShelves(newShelves);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const generateProducts = (data, shelfIndex) => {
    const realProducts = data.slice(shelfIndex * 10, (shelfIndex + 1) * 10);
    const fakeProducts = Array(10 - realProducts.length).fill().map((_, index) => ({
      id: `fake-${shelfIndex}-${index}`,
      product_name: `Fake Product ${shelfIndex * 10 + index + 1}`,
      barcode: `FAKE${shelfIndex}${index}`,
      image: `https://picsum.photos/200/300?random=${shelfIndex * 10 + index}`
    }));
    return [...realProducts, ...fakeProducts];
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;

    // Dropped outside the list
    if (!destination) {
      return;
    }

    const sourceShelfIndex = shelves.findIndex(shelf => shelf.id === source.droppableId);
    const destShelfIndex = shelves.findIndex(shelf => shelf.id === destination.droppableId);

    const newShelves = Array.from(shelves);
    const [reorderedItem] = newShelves[sourceShelfIndex].products.splice(source.index, 1);
    newShelves[destShelfIndex].products.splice(destination.index, 0, reorderedItem);

    setShelves(newShelves);
  };

  return (
    <div>
      <Title level={2}>Shelf Management</Title>
      <DragDropContext onDragEnd={onDragEnd}>
        <Row gutter={[16, 16]}>
          {shelves.map(shelf => (
            <Col span={8} key={shelf.id}>
              <Card title={shelf.name}>
                <Droppable droppableId={shelf.id}>
                  {(provided) => (
                    <Row gutter={[8, 8]} {...provided.droppableProps} ref={provided.innerRef}>
                      {shelf.products.map((product, index) => (
                        <Draggable key={product.id} draggableId={product.id} index={index}>
                          {(provided) => (
                            <Col span={8} ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                              <Card
                                hoverable
                                cover={<Image 
                                  src={product.image} 
                                  alt={product.product_name}
                                  fallback="data:image/png;base64,"
                                />}
                              >
                                <Card.Meta
                                  title={product.product_name}
                                  description={`Barcode: ${product.barcode}`}
                                />
                              </Card>
                            </Col>
                          )}
                        </Draggable>
                      ))}
                      {provided.placeholder}
                    </Row>
                  )}
                </Droppable>
              </Card>
            </Col>
          ))}
        </Row>
      </DragDropContext>
    </div>
  );
};

export default ShelfManagement;
